hist = [0] * 1000005

def solve(n, s):
    tot = cur = i = 0
    while i < n:
        if s[i] == '0':
            tot += cur
        else:
            l = r = i
            while r + 1 < n and s[r + 1] == '1':
                r += 1
            for x in range(r - l + 1):
                cur += l + x + 1 - hist[x]
                tot += cur
                hist[x] = r - x + 1
            i = r
        i += 1
    return tot

n = int(input().strip())
s = input().strip()
print(solve(n, s))
